import React from 'react';
import axios from 'axios';

const UserBooks = ({ userBooks, fetchUserBooks }) => {

  const handleRemoveFromMyList = async (bookId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(
        'http://localhost:5000/user/books',
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          data: { bookId }
        }
      );
      alert('Book removed from list.');
      fetchUserBooks();
    } catch (error) {
      console.error('Error removing book from list:', error);
      alert('Failed to remove book from list.');
    }
  };

  return (
    <div className="book-list">
      {userBooks.map((book) => (
        <div key={book.id} className="book-card">
          <img src={book.image_url} alt={book.title} />
          <h3>{book.title}</h3>
          <p>{book.author}</p>
          <p>{book.genre.genre}</p>
          <button onClick={() => handleRemoveFromMyList(book.id)}>Remove from My List</button>
        </div>
      ))}
    </div>
  );
};

export default UserBooks;
