import React, { useEffect, useState } from 'react';
import ClipLoader from 'react-spinners/ClipLoader';
import { Link } from 'react-router-dom';
import StarRatingComponent from 'react-star-rating-component';
import axios from './axiosConfig';

const UserBooks = ({ userBooks, onRemoveFromMyList, ratings, updateRating }) => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (userBooks) {
      setLoading(false);
    }
  }, [userBooks]);

  const handleRatingChange = async (bookId, nextValue) => {
    const token = localStorage.getItem('token');
    try {
      await axios.put(
        `http://localhost:5000/user/books/${bookId}`,
        { rating: nextValue },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      updateRating(bookId, nextValue);
    } catch (error) {
      console.error('Error updating rating:', error);
    }
  };

  return (
    <div className="user-books-container">
      {loading ? (
        <ClipLoader size={50} color={"#007bff"} />
      ) : (
        userBooks.length === 0 ? (
          <div className="empty-list-message">
            <p>Your book list is currently empty. To add some books, go to <Link to="/">home page</Link>.</p>
          </div>
        ) : (
          userBooks.map(book => (
            <div key={book.id} className="book-card">
              <img src={book.image_url} alt={book.title} />
              <h3>{book.title}</h3>
              <p>{book.author}</p>
              <label>
                Rating:
                <StarRatingComponent 
                  name={`bookRating-${book.id}`} 
                  starCount={5}
                  value={ratings[book.id] || 0}
                  onStarClick={(nextValue) => handleRatingChange(book.id, nextValue)}
                />
              </label>
              <button onClick={() => onRemoveFromMyList(book.id)}>Remove from List</button>
            </div>
          ))
        )
      )}
    </div>
  );
};

export default UserBooks;
