import React from 'react';
import BookCard from './BookCard';

const HomePage = ({ books, searchTerm, onSearch, onAddToMyList, userBooks, genres, selectedGenre, onGenreChange }) => {
  const handleGenreChange = (e) => {
    onGenreChange(e.target.value);
  };

  const filteredBooks = books.filter((book) => {
    return (
      book.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (selectedGenre === 'all' || book.genre.name === selectedGenre)
    );
  });

  return (
    <div className="home-page">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search books..."
          value={searchTerm}
          onChange={(e) => onSearch(e.target.value)}
        />
        <select value={selectedGenre} onChange={handleGenreChange}>
          <option value="all">All Genres</option>
          {genres.map((genre) => (
            <option key={genre.id} value={genre.name}>{genre.name}</option>
          ))}
        </select>
      </div>
      <div className="book-list">
        {filteredBooks.map((book) => (
          <BookCard
            key={book.id}
            book={book}
            onAddToMyList={onAddToMyList}
            isInUserBooks={userBooks.some((userBook) => userBook.id === book.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
