import React from 'react';
import './BookCard.css';

const BookCard = ({ books, searchTerm, onAddToMyList, userBooks }) => {
  const isBookInUserBooks = (bookId) => {
    return userBooks.some(book => book.id === bookId);
  };

  return (
    <div className="book-card-container">
      {books.filter(book => book.title.toLowerCase().includes(searchTerm.toLowerCase())).map((book) => (
        <div key={book.id} className="book-card">
          <img src={book.image_url} alt={book.title} />
          <h3>{book.title}</h3>
          <p>{book.author}</p>
          <p>{book.genre.genre}</p>
          <button 
            onClick={() => onAddToMyList(book.id)} 
            disabled={isBookInUserBooks(book.id)}
          >
            {isBookInUserBooks(book.id) ? "Added to My List" : "Add to My List"}
          </button>
        </div>
      ))}
    </div>
  );
};

export default BookCard;
