import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import './BookDetails.css'; // Make sure to import the CSS for styling

const BookDetails = () => {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [review, setReview] = useState('');
  const [reviews, setReviews] = useState([]);
  const [isReviewFormVisible, setReviewFormVisible] = useState(false);
  const [userName, setUserName] = useState('');

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/books/${id}`);
        setBook(response.data);
        setReviews(response.data.reviews || []);
      } catch (error) {
        console.error('Error fetching book:', error);
      }
    };

    fetchBook();

    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUserName(decoded.name); // Assuming the user's name is stored in the 'name' field of the token
      } catch (error) {
        console.error('Error decoding token:', error);
      }
    }
  }, [id]);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (token) {
      try {
        await axios.post(
          'http://localhost:5000/reviews',
          { book_id: book.id, review },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setReviews([...reviews, { body: review, user: { name: userName } }]);
        setReview('');
        setReviewFormVisible(false);
      } catch (error) {
        console.error('Error adding review:', error);
      }
    } else {
      alert('You need to be logged in to add a review.');
    }
  };

  return (
    <div className="book-details-container">
      {book ? (
        <>
          <div className="book-card">
            <img src={book.image_url} alt={book.title} className="book-image" />
            <h2>{book.title}</h2>
            <p>{book.author}</p>
            <p>{book.description}</p>
            <button onClick={() => setReviewFormVisible(true)}>Add a Review</button>
          </div>
          <div className="reviews-section">
            <h3>Reviews</h3>
            {isReviewFormVisible && (
              <form onSubmit={handleReviewSubmit} className="review-form">
                <textarea
                  value={review}
                  onChange={(e) => setReview(e.target.value)}
                  placeholder="Write your review here"
                />
                <button type="submit">Submit Review</button>
              </form>
            )}
            <ul className="reviews-list">
              {reviews.map((r, index) => (
                <li key={index} className="review-item">
                  <p><strong>{r.user.name}:</strong> {r.body}</p>
                </li>
              ))}
            </ul>
          </div>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default BookDetails;
