import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, useHistory, Link } from 'react-router-dom';
import axios from "./axiosConfig";
import { jwtDecode } from 'jwt-decode';
import HomePage from './HomePage';
import Register from './Register';
import Login from './Login';
import UserBooks from './UserBooks';
import BookDetails from './BookDetails';
import UserReviews from './UserReviews';
import AboutUs from './AboutUs';
import Recommendations from './Recommendations';
import NavBar from './NavBar';

const App = () => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [user, setUser] = useState(null);
  const [userBooks, setUserBooks] = useState([]);
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('');
  const [ratings, setRatings] = useState({});
  const history = useHistory();

  useEffect(() => {
    axios.get('http://localhost:5000/books').then((response) => {
      setBooks(response.data);
    }).catch((error) => {
      console.error('Error fetching books:', error);
    });

    axios.get('http://localhost:5000/genres').then((response) => {
      setGenres(response.data);
    }).catch((error) => {
      console.error('Error fetching genres:', error);
    });

    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUser(decoded);
      } catch (error) {
        console.error('Error decoding token:', error);
        localStorage.removeItem('token');
      }
    }
  }, []);

  useEffect(() => {
    if (user) {
      fetchUserBooks();
    }
  }, [user]);

  const fetchUserBooks = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get('http://localhost:5000/user/books', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUserBooks(response.data);
      const ratingsMap = response.data.reduce((acc, book) => {
        acc[book.id] = book.rating;
        return acc;
      }, {});
      setRatings(ratingsMap);
    } catch (error) {
      console.error('Error fetching user books:', error);
    }
  };

  const handleSearch = (term) => {
    setSearchTerm(term);
  };

  const handleGenreChange = (genre) => {
    setSelectedGenre(genre);
  };

  const handleRegister = async (name, password) => {
    try {
      const response = await axios.post('http://localhost:5000/register', { name, password }, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      console.log('Registration response:', response);
    } catch (error) {
      console.error('Registration failed:', error);
      alert('Registration failed: ' + error.response.data.message);
    }
  };

  const handleLogin = async (name, password) => {
    try {
      const response = await axios.post('/login', { name, password });
      const { access_token, refresh_token } = response.data;
      localStorage.setItem('token', access_token);
      localStorage.setItem('refresh_token', refresh_token);
      const decoded = jwtDecode(access_token);
      setUser(decoded);
      history.push('/');
      window.location.reload()
    } catch (error) {
      alert('Login failed: ' + error.response.data.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setUserBooks([]);
  };

  const addToMyList = async (bookId) => {
    if (user) {
      try {
        const token = localStorage.getItem('token');
        await axios.post(
          `http://localhost:5000/user/books`,
          { bookId },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setUserBooks([...userBooks, books.find(book => book.id === bookId)]);
      } catch (error) {
        console.error('Error adding book to list:', error);
      }
    } else {
      alert('You need to be logged in to add books to your list.');
    }
  };

  const removeFromMyList = async (bookId) => {
    if (user) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(
          `http://localhost:5000/user/books/${bookId}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setUserBooks(userBooks.filter(book => book.id !== bookId));
      } catch (error) {
        console.error('Error removing book from list:', error);
        alert('Failed to remove book from list.');
      }
    } else {
      alert('You need to be logged in to remove books from your list.');
    }
  };

  const updateRating = (bookId, rating) => {
    setRatings({ ...ratings, [bookId]: rating });
  };

  return (
    <Router>
      <div>
        <NavBar user={user} handleLogout={handleLogout} />
        <Switch>
          <Route path="/" exact>
            <HomePage
              onSearch={handleSearch}
              books={books}
              searchTerm={searchTerm}
              onAddToMyList={addToMyList}
              userBooks={userBooks}
              genres={genres}
              selectedGenre={selectedGenre}
              onGenreChange={handleGenreChange}
            />
          </Route>
          <Route path="/about" exact>
            <AboutUs /> 
          </Route>
          <Route path="/books/:id" exact>
            <BookDetails
              onAddToMyList={addToMyList}
              userBooks={userBooks}
              ratings={ratings}
              updateRating={updateRating}
            />
          </Route>
          <Route path="/register">
            <Register onRegister={handleRegister} />
          </Route>
          <Route path="/login">
            <Login onLogin={handleLogin} />
          </Route>
          <Route path="/user/books">
  {user ? (
    <UserBooks
      userBooks={userBooks}
      onRemoveFromMyList={removeFromMyList}
      ratings={ratings}
      updateRating={updateRating}
    />
  ) : (
    <div>
      <p>You need to be logged in to create your book list, please go to the <Link to="/login">login page</Link>.</p>
    </div>
  )}
</Route>
          <Route path="/user/reviews">
            {user ? (
               <UserReviews
                />
            ): (
            <Login onLogin={handleLogin} />
            )}
          </Route>
          <Route path="/recommendations">
            <Recommendations user={user} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
