import React from 'react';

const UserBooks = ({ userBooks, removeFromMyList }) => {
  return (
    <div className="user-book-list">
      {userBooks.length === 0 ? (
        <p>No books in your list.</p>
      ) : (
        userBooks.map(book => (
          <div key={book.id} className="book-card">
            <img src={book.image_url} alt={book.title} />
            <h3>{book.title}</h3>
            <p>{book.author}</p>
            <p>{book.genre?.genre}</p> {/* Ensure genre is correctly accessed */}
            <button onClick={() => removeFromMyList(book.id)}>Remove from My List</button>
          </div>
        ))
      )}
    </div>
  );
};

export default UserBooks;
