import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './UserReviews.css';

const UserReviews = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingReviewId, setEditingReviewId] = useState(null);
  const [editReviewBody, setEditReviewBody] = useState('');

  useEffect(() => {
    fetchReviews();
  }, []);

  const fetchReviews = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get('http://localhost:5000/user/reviews', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setReviews(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching reviews:', error);
      setLoading(false);
    }
  };

  const handleDelete = async (reviewId) => {
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`http://localhost:5000/reviews/${reviewId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setReviews(reviews.filter(review => review.id !== reviewId));
    } catch (error) {
      console.error('Error deleting review:', error);
    }
  };

  const handleEdit = async (reviewId) => {
    const token = localStorage.getItem('token');
    try {
      await axios.patch(
        `http://localhost:5000/reviews/${reviewId}`,
        { body: editReviewBody },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setReviews(reviews.map(review => review.id === reviewId ? { ...review, body: editReviewBody } : review));
      setEditingReviewId(null);
      setEditReviewBody('');
    } catch (error) {
      console.error('Error editing review:', error);
    }
  };

  if (loading) {
    return <div className="spinner">Loading...</div>;
  }

  if (reviews.length === 0) {
    return (
      <div className="empty-reviews-message">
        <p>Your review list is currently empty. To add some reviews, go to <Link to="/">home page</Link> and choose a book.</p>
      </div>
    );
  }

  return (
    <div className="user-reviews-container">
      {reviews.map((review) => (
        <div key={review.id} className="review-card">
          <h3>{review.book.title}</h3>
          {editingReviewId === review.id ? (
            <div>
              <textarea
                value={editReviewBody}
                onChange={(e) => setEditReviewBody(e.target.value)}
              />
              <button onClick={() => handleEdit(review.id)}>Save</button>
              <button onClick={() => setEditingReviewId(null)}>Cancel</button>
            </div>
          ) : (
            <div>
              <p>{review.body}</p>
              <button onClick={() => {
                setEditingReviewId(review.id);
                setEditReviewBody(review.body);
              }}>Edit</button>
              <button onClick={() => handleDelete(review.id)}>Delete</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default UserReviews;
