import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link, useHistory } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import HomePage from './HomePage';
import BookCard from './BookCard';
import BookDetails from './BookDetails';
import Register from './Register';
import Login from './Login';
import UserBooks from './UserBooks';

const App = () => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/books').then((response) => {
      setBooks(response.data);
    });
  }, []);

  const handleSearch = (term) => {
    setSearchTerm(term);
  };

  const handleRegister = async (name, password) => {
    try {
      await axios.post('http://localhost:5000/register', { name, password });
      alert('Registration successful!');
    } catch (error) {
      alert('Registration failed: ' + error.response.data.message);
    }
  };

  const handleLogin = async (name, password) => {
    try {
      const response = await axios.post('http://localhost:5000/login', { name, password });
      const { access_token } = response.data;
      localStorage.setItem('token', access_token);
      const decoded = jwtDecode(access_token);
      setUser(decoded);
      alert('Login successful!');
      navigate('/');
    } catch (error) {
      alert('Login failed: ' + error.response.data.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    alert('Logged out successfully!');
  };

  return (
    <div>
      <nav>
        <Link to="/">Home</Link> | <Link to="/books">Books</Link>
        {user ? (
          <>
            | <Link to="/user/books">My Books</Link> |{' '}
            <button onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <>
            | <Link to="/register">Register</Link> | <Link to="/login">Login</Link>
          </>
        )}
      </nav>
      <Routes>
        <Route path="/" element={<HomePage onSearch={handleSearch} />} />
        <Route path="/books" element={<BookCard books={books} searchTerm={searchTerm} />} />
        <Route path="/books/:id" element={<BookDetails />} />
        <Route path="/register" element={<Register onRegister={handleRegister} />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/user/books" element={user ? <UserBooks user={user} /> : <Login onLogin={handleLogin} />} />
      </Routes>
    </div>
  );
};

export default App;
