import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Recommendations.css';
import { jwtDecode } from 'jwt-decode';

const Recommendations = ({ user }) => {
  const [recommendations, setRecommendations] = useState([]);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [userName, setUserName] = useState('');

  useEffect(() => {
    const fetchRecommendations = async () => {
      try {
        const response = await axios.get('http://localhost:5000/recommendations');
        setRecommendations(response.data);
      } catch (error) {
        console.error('Error fetching recommendations:', error);
      }
    };

    fetchRecommendations();
  }, []);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decoded = jwtDecode(token);
      setUserName(decoded.name);  // Assuming 'name' is a property in the token payload
    }
  }, []);

  const handleRecommendationSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const response = await axios.post(
          'http://localhost:5000/recommendations',
          { title, author },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setRecommendations([...recommendations, { ...response.data, user: { name: userName } }]);
        setTitle('');
        setAuthor('');
      } catch (error) {
        console.error('Error submitting recommendation:', error);
      }
    } else {
      alert('You need to be logged in to submit a recommendation.');
    }
  };

  return (
    <div className="recommendations-container">
      <h2>Book Recommendations</h2>
      {user && (
        <form onSubmit={handleRecommendationSubmit}>
          <input
            type="text"
            placeholder="Book Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Author"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
            required
          />
          <button type="submit">Submit Recommendation</button>
        </form>
      )}
      <div className="recommendations-list">
        {recommendations.map((rec) => (
          <div key={rec.id} className="recommendation-card">
            <h3>{rec.title}</h3>
            <p>{rec.author}</p>
            <p className="recommendation-author">Recommended by: {rec.user.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Recommendations;
