import React from 'react';
import './BookCard.css';

const BookCard = ({ books, searchTerm, onAddToMyList, userBooks }) => {
  const filteredBooks = books.filter(book =>
    book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    book.author.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="book-list">
      {filteredBooks.map(book => (
        <div key={book.id} className="book-card">
          <img src={book.image_url} alt={book.title} />
          <h3>{book.title}</h3>
          <p>{book.author}</p>
          <p>{book.genre?.genre}</p> {/* Ensure genre is correctly accessed */}
          {userBooks.some(userBook => userBook.id === book.id) ? (
            <button disabled>Book Added</button>
          ) : (
            <button onClick={() => onAddToMyList(book.id)}>Add to My List</button>
          )}
        </div>
      ))}
    </div>
  );
};

export default BookCard;
